import React, { useState, useEffect } from 'react';
import './App.css';
import Project from './components/Project';
import Experience from './components/Experience';
import TimelineEvent from './components/TimelineEvent';
import FallingStars from './components/FallingStars';  // Import the FallingStars component
import 'font-awesome/css/font-awesome.min.css';

function App() {
  const [bgColor, setBgColor] = useState('rgba(0, 0, 0, 1)');
  const [theme, setTheme] = useState(''); // Add this line for theme change
  const [timelineEvents, setTimelineEvents] = useState([]);
  const [activeTimeline, setActiveTimeline] = useState('projects'); // 'projects', 'volunteer', or 'experiences'

  useEffect(() => {
    // Define your sets of timeline events
    const projectEvents = [
      {
        type: "iOS Developer",
        title: "Social iOS App",
        date: 'Aug 2023 - Present',
        description: 'Incorporated Firebase for storage & login authentication. Integrated real-time data syncing between local storage and Cloud Firestore, ensuring a seamless user experience. Implemented the MVVM design pattern for a scalable and maintainable codebase.',
        skills: ['SwiftUI', 'Cloud Firestore', 'Firebase', 'Data Synchronization', 'Model-view-viewmodel (MVVM)', 'Swift (Programming Language)'],
        image: 'image7.png',
        gallery: ['image7.png', 'image8.png']
      },
      {
        type: "Full-stack Developer",
        title: "Algorithm Visualizer",
        date: 'Mar 2022 - May 2022',
        description: 'Visualized BST, Min/Max Heap operations. Backend: JS & Node.js | Frontend: HTML & CSS. Built an Undo feature using a stack of deep copied history. Implemented a BFS-based node positioning algorithm for binary tree illustration. Created a "current action" box, showing the current action in the algorithm in real-time.',
        skills: ['JS', 'Node.js', 'HTML', 'CSS'],
        image: 'image9.png',
        gallery: ['image9.png', 'image10.png']
      },
      {
        type: "Data Scientist",
        title: "Data Science Tutorial",
        date: 'Nov 2021 - Dec 2021',
        description: 'Created a step-by-step introduction to data science using Jupyter Notebook. Walked the reader through Data Collection, Processing, Visualization, and Machine Learning. Showcased the steps needed to tidy different types of tables into easy to manipulate data frames. Demonstrated skewed data handling techniques, like logarithmic representation and trimming outliers. Presented hypothesis testing and prediction with single-variable vs multivariable modules.',
        skills: ['Python', 'Pandas', 'Jupyter', 'Data Visualization', 'Machine Learning'],
        image: 'image11.png',
        gallery: ['image11.png', 'image12.png']
      },
      {
        type: "Python Developer",
        title: "Maze Generator",
        date: 'Aug 2020',
        description: 'Created a visualized random maze generator with the Pygame library. Implemented the randomized depth-first search (DFS) method.',
        skills: ['Python', 'Pygame', 'DFS'],
        image: 'image13.png',
        gallery: ['image13.png', 'image14.png']
      }
    ];


    const volunteerEvents = [
      {type: "Insect Whisperer", title: "Talked to a Fly", date: '2023, Aug', description: 'Had a deep conversation with a fly about the meaning of buzz.', image: 'image4.png', gallery: ['image5.png', 'image6.png']},
      {type: "Bathroom Cleaner", title: "Cleaned the Cleaner's Bathroom", date: '2023, Jul', description: 'Found a cleaner and cleaned his bathroom.', image: 'image3.png', gallery: ['image3.png', 'image4.png', 'image5.png', 'image6.png']},
      // ...
    ];

    const experienceEvents = [
      {type: "Chicken Eater", title: "Won Chicken-Eating Contest", date: '2023, May', description: 'Ate 100 chickens in 5 minutes.', image: 'image1.png', gallery: ['image1.png', 'image2.png']},
      {type: "Frog Mentor", title: "Taught Frogs How to Chicken", date: '2023, Jun', description: 'Organized a seminar where frogs learned the art of eating chicken.', image: 'image2.png', gallery: ['image3.png', 'image3.png']},
      // ...
    ];

    // Set timelineEvents based on activeTimeline
    if (activeTimeline === 'projects') {
      setTimelineEvents(projectEvents);
    } else if (activeTimeline === 'volunteer') {
      setTimelineEvents(volunteerEvents);
    } else if (activeTimeline === 'experiences') {
      setTimelineEvents(experienceEvents);
    }
  }, [activeTimeline]);



  useEffect(() => {
    document.body.style.backgroundColor = bgColor;
    return () => {
      document.body.style.backgroundColor = null;
    };
  }, [bgColor]);


  useEffect(() => {
    const updateBackground = (e) => {
      const x = e.clientX;
      const y = e.clientY;
      const z = (x + y) / 2;

      // Here we are reducing the range to 200-255, which limits the color change
      const red = Math.floor((x / window.innerWidth) * 55 + 200);
      const green = Math.floor((y / window.innerHeight) * 55 + 200);
      const blue = Math.floor((z / ((window.innerWidth + window.innerHeight) / 2)) * 55 + 200);

      setBgColor(`rgba(${red}, ${green}, ${blue}, 0.5)`);  // Alpha set to 0.5 for transparency
    };


    const toggleNightTheme = (e) => {
      if (e.clientX > window.innerWidth / 2) {
        setTheme('App-night');
      } else {
        setTheme('');
      }
    }

    //window.addEventListener('mousemove', updateBackground);
    //window.addEventListener('mousemove', toggleNightTheme);  // Add this line for theme change

    return () => {
      //window.removeEventListener('mousemove', updateBackground);
      //window.removeEventListener('mousemove', toggleNightTheme); // Add this line for theme change
    };
  }, []);

  //const [headerColor, setHeaderColor] = useState("#282c34"); // State to keep track of the header color

  const handleMouseEnter = () => {
    //setHeaderColor("#444"); // Change to a different color when the mouse enters
  };

  const handleMouseLeave = () => {
    //setHeaderColor("#282c34"); // Reset to the original color when the mouse leaves
  };
  //<FallingStars /> {/* Include the FallingStars component */}
  // put this below to make stars

  return (
    <div className={`App ${theme}`} style={{ backgroundColor: bgColor }}>


    <header className="App-header" >
      <h1 className="title-english">Taha Al-Nufaili</h1>

      <div className="TabMenu">
        <button 
          className={activeTimeline === 'projects' ? 'active' : ''} 
          onClick={() => setActiveTimeline('projects')}
        >
          Projects
        </button>
        <button 
          className={activeTimeline === 'volunteer' ? 'active' : ''} 
          onClick={() => setActiveTimeline('volunteer')}
        >
          Volunteer
        </button>
        <button 
          className={activeTimeline === 'experiences' ? 'active' : ''} 
          onClick={() => setActiveTimeline('experiences')}
        >
          Experiences
        </button>
      </div>


      <h1 className="title-arabic">ÿ∑Ÿá ÿßŸÑŸÜŸÅŸäŸÑŸä</h1>

        <div className="social-icons">
          <a href="https://www.youtube.com/@tahaalnufaili8838" target="https://www.youtube.com/@tahaalnufaili8838" rel="noopener noreferrer">
            <i className="fa fa-youtube fa-2x"></i>
          </a>
          <a href="https://www.linkedin.com/in/taha-al-nufaili/" target="https://www.linkedin.com/in/taha-al-nufaili/" rel="noopener noreferrer">
            <i className="fa fa-linkedin fa-2x"></i>
          </a>
          <a href="https://github.com/ta0a2000t" target="https://github.com/ta0a2000t" rel="noopener noreferrer">
            <i className="fa fa-github fa-2x"></i>
          </a>
          <a href="https://www.youtube.com/watch?v=7eAdsUbXkaQ&ab_channel=MrMahir%D9%85%D8%A7%D9%87%D8%B1" target="resume.pdf" rel="noopener noreferrer">  {/* Update this URL to point to your actual resume */}
            <i className="fa fa-file-text fa-2x"></i>
          </a>
        </div>

    </header>




      <section className="Timeline">
        <h2>{activeTimeline === 'projects' ? 'My Projects üõ†' : activeTimeline === 'volunteer' ? 'My Volunteer Work ü§ù' : 'My Experiences üõ∂'}</h2>
        {timelineEvents.map((event, index) => (
          <TimelineEvent key={index} event={event} />
        ))}
      </section>


    </div>
  );
}

export default App;
